mapper.select:
select *
from "activity_application"
where deleted = false and id = #{id};

select count(1)
from "activity_application"
where deleted = false and created_by = #{userId};

select *
from "activity_application"
where deleted = false and created_by = #{userId};

mapper.update:
update "activity_application"
set audit_status = #{audit_status}
where deleted = false and id = #{id};
