mapper.update:
update "user"
set points = points + #{points}
where deleted = false and id = #{id};

update "user"
set openid = #{openid}
where deleted = false and id = #{id};

update "user"
set points = points - #{points}
where deleted = false and id = #{id};

mapper.select:
select *
from "user"
where deleted = false and username = #{username};

select *
from "user"
where deleted = false and openid = #{openid};

select *
from "user"
where deleted = false and id = #{id};

select *
from "user"
where deleted = false and username = #{username} and password = #{password};
