mapper.update:
update user
set points = points + #{points}
where id = #{id};

update user
set points = points - #{points}
where id = #{id};

update user
set points = points + #{points}
where id = #{userId};

update user
set points = points - #{points}
where id = #{userId};

mapper.select:
select *
from user
where username = #{username}
  and password = #{password};
